import { RouterProvider, createBrowserRouter } from 'react-router-dom'
import HomePage from './pages/HomePage';
import RootPage from './pages/RootPage';
import EventsPage, { loader as eventsLoader } from './pages/event/Events';
import NewEventPage from './pages/event/NewEventPage';
import EditEventPage from './pages/event/EditEventPage';
import EventDetailPage, { loader as eventDetailLoader, action as eventDetailAction } from './pages/event/EventDetailPage';
import EventsRoot from './pages/event/EventsRoot';
import ErrorPage from './pages/Error';
import {action as manipulateEventAction} from './components/EventForm';

const router = createBrowserRouter([
  {
    path: '/',
    element: <RootPage />,
    errorElement: <ErrorPage />,
    children: [
      { index: true, element: <HomePage /> },
      {
        path: 'events',
        element: <EventsRoot />,
        children: [
          { index: true, element: <EventsPage />, loader: eventsLoader },
          {
            path: ':eventId',
            loader: eventDetailLoader,
            id: 'event-detail',
            children: [
              { index: true, element: <EventDetailPage />, action: eventDetailAction },
              { path: 'edit', element: <EditEventPage /> },
            ]
          },
          { path: 'new', element: <NewEventPage />, action: manipulateEventAction },

        ]
      },

    ]
  }
]);

function App() {
  return <RouterProvider router={router} />;
}

export default App;
